bttestOneS <- function(
    data,
    vars,
    testValue = 0,
    hypothesis = "different",
    bfType = "BF10",
    desc = FALSE,
    plotPP = FALSE,
    plotPPAddInfo = TRUE,
    plotRobust = FALSE,
