    infoNL = 0,
    infoNS = 0.707,
    infoTL = 0,
    infoTS = 0.707,
    infoTDf = 1,
    plotWidth = 320,
    plotHeight = 240) {

    if ( ! requireNamespace('jmvcore'))
        stop('bttestOneS requires jmvcore to be installed (restart may be required)')

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
