        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- bttestOneSOptions$new(
        vars = vars,
        testValue = testValue,
        hypothesis = hypothesis,
        bfType = bfType,
        desc = desc,
        plotPP = plotPP,
