        infoNL = infoNL,
        infoNS = infoNS,
        infoTL = infoTL,
        infoTS = infoTS,
        infoTDf = infoTDf,
        plotWidth = plotWidth,
        plotHeight = plotHeight)

    analysis <- bttestOneSClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
